import "./styles.css";
import { useAppDispatch, useAppSelector } from "./app/hooks.tsx";
import { incremented, addSome } from "./features/counter/counter-slice.tsx";
import { useState } from "react";
export default function App() {
  type inputState = {
    input: number;
  };
  const [amountToAdd, setamountToAdd] = useState<inputState>(null);
  const count = useAppSelector((state) => state.counter.value);
  const dispatch = useAppDispatch();

  const handleClick = () => {
    dispatch(incremented());
  };
  interface iAmount {
    amount: number;
  }
  const handleAddSome = (amount: iAmount) => {
    dispatch(addSome(amount));
  };
  return (
    <div className="App">
      <h1>Counter example</h1>

      <p>Count: {count}</p>
      <button onClick={handleClick}>Increment+</button>

      <hr />
      <p>choose amount</p>
      <fieldset>
        <input type="number" />
        <button onClick={() => handleAddSome()}>Add</button>
      </fieldset>
    </div>
  );
}
